import axios from 'axios';
import { FETCH_USER, FETCH_SURVEYS } from './types';




export const fetchUser = () => async dispatch => {
  const res = await axios.get('/api/current_user');

  dispatch({ type: FETCH_USER, payload: res.data });
};

export const handleToken = token => async dispatch => {
  const res = await axios.post('/api/stripe', token);

  dispatch({ type: FETCH_USER, payload: res.data });
};

export const submitSurvey = (values, history) => async dispatch => {
  const res = await axios.post('/api/surveys', values);
  
  
  dispatch({ type: FETCH_USER, payload: res.data });
  history.push('/surveys')
};

// To edit a stream we first need to fetch the survey.
export const fetchSurvey = async (id) => {
  const res = await axios.get('/api/surveys');
  const survey = res.data.filter(response => response._id === id);
  //const result = await axios.post(`/api/surveys/${id}`);
  console.log(survey);
  return survey;
}

export const submitEdit = (values, history)  => async (dispatch) =>{
  console.log(values);
  dispatch({ type: FETCH_USER, payload: null });
  history.push('/surveys')
};

export const deleteSurvey =  (id, history) => async dispatch => {
  //const res = await Survey.findOneAndDelete({_id: id}, { new: true});
  // history.push('/surveys');


  console.log(id);
  dispatch({ type: FETCH_USER, payload: null });
}


export const fetchSurveys = () => async dispatch => {
  const res = await axios.get('/api/surveys');
  console.log(res.data);
  dispatch({ type: FETCH_SURVEYS, payload: res.data })
};
