import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchSurveys, deleteSurvey } from '../../actions';

class SurveyList extends Component {
  componentDidMount() {
    this.props.fetchSurveys();
  }

  renderSurveys() {
    return this.props.surveys.reverse().map(survey => {
      return (

        <div className="card blue-grey darken-1" key={survey._id}>
          {console.log((survey._id))}
          <div className="card-content white-text">
            <span className="card-title" style = {{ fontSize: 'xx-large'}}>{survey.title}</span>
            <p style = {{ fontSize: 'large'}}>
              {survey.body}
            </p>
            <p className="right" style = {{ fontSize: 'large'}}>
              Sent On: {new Date(survey.dateSent).toLocaleDateString()},
              <span> </span>
              Last Responded: {new Date(survey.lastResponded).toLocaleDateString()}
            </p>
          </div>
          <div className="card-action white-text">
            <a>Yes: {survey.yes}</a>
            <a>No: {survey.no}</a>
          </div>
          <div className = "card-action">
            {/* <a className = "btn" href = {`/surveys/edit/${survey._id}`} >Edit Survey</a>
            <span> </span> */}
            <a className = "btn red" onClick = {() => this.props.deleteSurvey(survey._id)} href = "/surveys">Delete Survey</a>
          </div>
            

          
              
        </div>
      );
    });
  }

  

  render() {
    return (
      <div>
        <button>Sort By Date Sent</button>
        {this.renderSurveys()}
      </div>
    );
  }
}


function mapStateToProps({ surveys }) {
  return { surveys };
}
// Comment
export default connect(mapStateToProps, { fetchSurveys, deleteSurvey })(SurveyList);
